         }
     }

     if (root.InspectionPackage.HiddenSlides != null)
     {
         foreach (var slide in root.InspectionPackage.HiddenSlides)
         {
             Console.WriteLine(slide.Name);
             Console.WriteLine(slide.Number);
             Console.WriteLine(slide.SlideId);
         }
     }
}


